def main():    start_timer()    show('Welcome to the experiment!').now()    show_message('Press space to continue').now()    yield for_space()    show('Answer the questions!').now()    yield for_space()    for i in range(3):        num_correct=0        for i in range(5):            start_timer()            pick_question()            record(question())            record(answer())            show(question()).now()            show_message('Press space to answer the question').now()            bug = show_bug(answer()).after(1000)            yield for_space()            clear_message().now()            clear_bug().now()            bug.stop()            record(time())            yield for_user_input()            record(time())            record(user_input())            if user_input() == answer():                num_correct += 1            clear().now()            show_message('Press space to view next question').now()            yield for_space()        clear().now()        bug = show_bug(num_correct).after(500)        record(num_correct)        show_message('Press space to continue').now()        yield for_space()        clear_bug().now()        bug.stop()    show('All done!')    show_message('Press space to quit.')    yield for_space()