def main():    start_timer()    show('The experiment is running').now()    show_message('Press space to continue').now()    yield for_space()    t=time()    record(t)    show('I am now accepting input').now()    show_bug('It took you '+str(t)+' seconds to press space').after(1000)    yield for_user_input()    record(user_input())    record(time())    show('You entered '+user_input()).now()    yield for_space()